import React, { useState , useEffect} from 'react';
import './App.css';

function toggle(){
    const[toggle,settoggle]=useState(false);

   useEffect(()=>{
        document.body.style.backgroundColor=toggle?"blue":"black";
        document.body.style.color=toggle?"white":"black";
    },[isDarkMode]);

    const toggleMode=()=>{
        settoggle((preMode)=>!preMode);
    };

    return (
        <div style={{ textAlign: "center", marginTop: "100px" }}>
          <h1>{toggle ? "Dark Mode" : "Light Mode"}</h1>
          <button onClick={toggleMode}>
            Switch to {toggle ? "Light" : "Dark"} Mode
          </button>
        </div>
      );
}
export default toggle;
