import React, { useState, useEffect } from "react";
import "./App.css";

function Cargame() {
  const [position, setPosition] = useState({ top: 200, left: 200 });

  const handleKeyDown = (e) => {
    setPosition((prev) => {
      const step = 10;
      switch (e.key) {
        case "ArrowUp":
          return { ...prev, top: prev.top - step };
        case "ArrowDown":
          return { ...prev, top: prev.top + step };
        case "ArrowLeft":
          return { ...prev, left: prev.left - step };
        case "ArrowRight":
          return { ...prev, left: prev.left + step };
        default:
          return prev;
      }
    });
  };

  useEffect(() => {
    window.addEventListener("keydown", handleKeyDown);
    return () => window.removeEventListener("keydown", handleKeyDown);
  }, []);

  return (
    <div className="game-area">
      <div
        className="car"
        style={{
          top: `${position.top}px`,
          left: `${position.left}px`,
        }}
      ></div>
      <h3>Use Arrow Keys to Move the Car</h3>
    </div>
  );
}

export default App;
