import React, { useState } from 'react';
import './App.css';

function App() {
  const [count, setCount] = useState(10);

  const increment = () => {
    setCount(count + 2 );
  };

  const decrement = () => {
    setCount(count - 1);
  };

  const reset = () => {
    setCount(0);
  };

  return (
    <>
        <h1>Counter App</h1>
        <div className="counter-display">{count} value</div>
        <div className="button-container">
          <button onClick={decrement()} className="counter-button">-</button>
          <button onClick={reset} className="counter-button reset">Reset</button>
          <button onClick={increment} className="counter-button">+</button>
        </div>
        </>
  );
}

export default App;
